mapper.update:
UPDATE ANSWERS
ANSWER_CONTENT = #{answerContent}, ANSWER_UPDATE_DATE = SYSDATE, ANSWER_IMG = #{answerImg}, ANSWER_IMG = null
WHERE ANSWER_ID = #{answerId}

mapper.select:
	SELECT *
	FROM (SELECT ROWNUM AS rm, a.*
		  FROM (SELECT *
			    FROM answers


			    		AND question_id = #{questionId}


			    		AND member_id = #{memberId}



	                answer_id DESC
	                answer_id
	            ) a )
     WHERE rm BETWEEN #{startRow} AND #{endRow}

SELECT COUNT(*)
FROM answers
WHERE question_id = #{id}

SELECT COUNT(*)
FROM answers
WHERE member_id = #{id}

mapper.update:
UPDATE answers
SET answer_solve = 'solved'
WHERE answer_id = #{answerId}

UPDATE answers
SET answer_solve = 'not_solved'
WHERE answer_id = #{answerId}
