mapper.update:
UPDATE boards
board_title = #{boardTitle}, board_content = #{boardContent}, board_img = #{boardImg}, board_img = null, board_update_date = SYSDATE
WHERE board_id = #{boardId}

mapper.select:
SELECT * FROM (SELECT ROWNUM AS rm, a.* FROM (SELECT * FROM boards b
AND board_title LIKE '%' || #{searchContent} || '%'
AND board_content LIKE '%' || #{searchContent} || '%'
AND member_id LIKE '%' || #{searchContent} || '%'
AND member_id = #{memberId}
board_id DESC board_hit DESC
(SELECT COUNT(*) FROM recommends r WHERE r.board_id = b.board_id AND r.recommend_value = 'like') DESC, board_id DESC
) a ) WHERE rm BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE boards
SET board_hit = board_hit + 1
WHERE board_id = #{id}
