mapper.update:
UPDATE COURSES
COURSE_NAME = #{courseName}, COURSE_PRICE = #{coursePrice}, COURSE_MAIN_CATEGORY = #{courseMainCategory}, COURSE_SUB_CATEGORY = #{courseSubCategory}, COURSE_TEACHER = #{courseTeacher}, COURSE_GRADE = #{courseGrade}, COURSE_SCRIPT = #{courseScript}, COURSE_IMG = #{courseImg}, COURSE_IMG = NULL
WHERE COURSE_ID = #{courseId}
