mapper.select:
SELECT *
FROM MEMBERS
WHERE MEMBER_ID = #{memberId}

	AND MEMBER_PASSWORD = #{memberPassword}

mapper.update:
UPDATE MEMBERS
MEMBER_NAME = #{memberName}, MEMBER_TEL = #{memberTel}, MEMBER_ADDRESS = #{memberAddress}, MEMBER_EMAIL = #{memberEmail}, MEMBER_STOP_DATE = #{memberStopDate}
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT *
FROM MEMBERS
WHERE MEMBER_ID = #{memberId}
AND MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE MEMBERS

	MEMBER_PASSWORD = #{memberPassword}

WHERE MEMBER_ID = #{memberId}
AND MEMBER_EMAIL = #{memberEmail}
