mapper.select:
SELECT *
FROM PACKAGES
WHERE 1=1

	AND PACKAGE_CATEGORY = #{packageCategory}


	AND PACKAGE_GRADE = #{packageGrade}

mapper.update:
UPDATE PACKAGES
PACKAGE_TITLE = #{packageTitle}, PACKAGE_SCRIPT = #{packageScript}, PACKAGE_DISCOUNT = #{packageDiscount}, PACKAGE_THUMBNAIL = #{packageThumbnail}, PACKAGE_IMG = #{packageImg}, PACKAGE_CATEGORY = #{packageCategory}, PACKAGE_GRADE = #{packageGrade},
WHERE PACKAGE_ID = #{packageId}
