mapper.update:
UPDATE QUESTIONS
QUESTION_TITLE = #{questionTitle}, QUESTION_CONTENT = #{questionContent}, QUESTION_IMG = null QUESTION_IMG = #{questionImg}, QUESTION_UPDATE_DATE = SYSDATE
WHERE QUESTION_ID = #{questionId}

UPDATE questions
SET question_solve = #{questionSolve}
WHERE question_id = #{questionId}

UPDATE questions
SET question_hit = question_hit + 1
WHERE question_id = #{id}
