mapper.update:
UPDATE replies

	reply_content = #{replyContent},
	reply_update_date = SYSDATE;

WHERE reply_id = #{replyId}

mapper.select:
	SELECT *
	FROM (SELECT ROWNUM AS rm, a.*
		  FROM (SELECT *
			    FROM replies r
			    WHERE board_id = #{boardId}

	                reply_id DESC
	                reply_id
	            ) a )
     WHERE rm BETWEEN #{startRow} AND #{endRow}

SELECT COUNT(*)
FROM replies
WHERE board_id = #{id}
